INSERT INTO `ls_article` (`id`,`cid`,`title`,`synopsis`,`image`,`is_notice`,`content`,`visit`,`sort`,`is_show`,`create_time`,`update_time`,`del`,`likes`) VALUES (1,1,'电商商城管理规章制度','电商商城管理规章制度','/uploads/images/background/20201210/5b82112f4d294b11b80da87edd422772.jpg',1,'<p style=\"text-align: justify;\"><span class=\"bjh-p\">今年，以直播带货为代表的“内容+电商”成为淘宝、京东等电商平台和以央视为代表的媒体共同探索的变现新渠道。</span></p><p style=\"text-align: justify;\"><span class=\"bjh-p\">作为产业门类齐全、产业规模庞大的新型主流媒体及综合文化产业集团，SMG如何利用广电平台多渠道流量矩阵创新全域营销玩法？</span></p><p style=\"text-align: justify;\"><span class=\"bjh-p\">一切都在11月27日B+商城举办的“没内容，不电商”发布会上揭晓。从7月初成立至今，B+商城以东方购物视频电商为基础，依托SMG既有的相关服务能力和体系，面向都市成熟人群和家庭需求，正在向“魔都”生活第一入口的方向迈进。它的成长体现了SMG内容和渠道打通、大屏和小屏联动所发挥的巨大效应。</span></p><div><span class=\"bjh-p\"><br></span></div>',2,10,1,1607568471,NULL,0,0);
